mapper.select:
select 

from (



            select  

            from visit_log v 
                inner join device d on d.ID = v.DeviceID 
                inner join t_site s on s.ID = d.siteID
            where s.ID = #{item.siteID} 
                and v.StartTime >= #{item.startDate} 
                and v.StartTime <= #{item.endDate}
                and v.User_MAC != #{content}
            group by v.User_MAC

        ) c
            left join monitor m on m.MAC = c.Content 



            select  

            from hpl_log v 
                inner join device d on d.ID = v.DeviceID 
                inner join t_site s on s.ID = d.siteID
            where s.ID = #{item.siteID} 
                and v.RecordTime >= #{item.startDate} 
                and v.RecordTime <= #{item.endDate}
                and v.User_IMSI != #{content}
            group by v.User_IMSI

        ) c
            left join monitor m on m.IMSI = c.Content 



            select 

            from hpl_log v 
                inner join device d on d.ID = v.DeviceID 
                inner join t_site s on s.ID = d.siteID
            where s.ID = #{item.siteID} 
                and v.RecordTime >= #{item.startDate} 
                and v.RecordTime <= #{item.endDate}
                and v.User_IMEI != #{content}
            group by v.User_IMEI

        ) c
            left join monitor m on m.IMEI = c.Content 


group by c.Content


         ORDER BY ${orderField} ${orderDirection}


         order by TotalCount desc

select count(0) as c from (



            select v.User_MAC as Content from visit_log v 
                inner join device d on d.ID = v.DeviceID 
                inner join t_site s on s.ID = d.siteID 
            where s.ID = #{item.siteID} 
                and v.StartTime >= #{item.startDate} 
                and v.StartTime <= #{item.endDate} 
                and v.User_MAC != #{content}
            group by v.User_MAC




            select v.User_IMSI as Content from hpl_log v 
                inner join device d on d.ID = v.DeviceID 
                inner join t_site s on s.ID = d.siteID 
            where s.ID = #{item.siteID} 
                and v.RecordTime >= #{item.startDate} 
                and v.RecordTime <= #{item.endDate}
                and v.User_IMSI != #{content}
            group by v.User_IMSI




            select v.User_IMEI as Content from hpl_log v 
                inner join device d on d.ID = v.DeviceID 
                inner join t_site s on s.ID = d.siteID 
            where s.ID = #{item.siteID} 
                and v.RecordTime >= #{item.startDate} 
                and v.RecordTime <= #{item.endDate}
                and v.User_IMEI != #{content}
            group by v.User_IMEI



) a
